#ifndef __WHEEL_DATATYPE_H__
#define __WHEEL_DATATYPE_H__

#include <stdint.h> 

/**
* Structure to store the wheel data
* linear acceleration is in the units of g/sec^2
* angular velocity is in rad/sec
* temperature is in units of deg.C
*/
typedef struct
{
   /**
   * Timestamp in microseconds synced to same clock source for the camera
   * frame timestamp.
   * timestamp_in_us = raw_timestamp_in_us + ticks_imu_clock_behind_camera_clock
   */
   uint64_t timestamp;

   /**
   * Incremental counter on the IMU data, generated by the API.
   */
   uint32_t sequence_number;

   /**
   * wheel encoder data, raw or calibrated depending on the API used to get the data.
   * The units are m/sec
   */
   float    linear_velocity;

   /**
   * wheel encoder data, raw or calibrated based on the API used to get the data.
   * The units are rad/sec
   */
   float    angular_velocity;

   float    location[3];  //(x, y, z)
   float    direction[4]; //quatenion (x, y, z, w)


}  sensor_wheel;

#endif // !__WHEEL_DATATYPE_H__

